import React from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import { Box, Typography, AppBar, Toolbar, Grid, Container } from '@mui/material';
import MainInfo from './components/MainInfo';
import FinancialStatements from './components/FinancialStatements';
import BullBearDebate from './components/BullBearDebate';
import ChartSection from './components/ChartSection';
import PeerComparison from './components/PeerComparison';
import { Panel, PanelGroup, PanelResizeHandle } from 'react-resizable-panels';

const theme = createTheme({
  palette: {
    primary: {
      main: '#0071e3', // Apple blue
    },
    secondary: {
      main: '#1db954', // Apple green
    },
    background: {
      default: '#fff',
      paper: '#f5f5f7',
    },
    text: {
      primary: '#111',
      secondary: '#888',
    },
  },
  typography: {
    fontFamily: '"SF Pro Display", "Roboto", "Helvetica", "Arial", sans-serif',
    h4: {
      fontWeight: 700,
    },
    h5: {
      fontWeight: 600,
    },
  },
});

function App() {
  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Box sx={{ flexGrow: 1, minHeight: '100vh', bgcolor: 'background.default' }}>
        <AppBar position="static" elevation={0} sx={{ background: '#fff', color: '#111', borderBottom: '1px solid #e0e0e0' }}>
          <Toolbar>
            <Typography variant="h6" component="div" sx={{ flexGrow: 1, fontWeight: 700, letterSpacing: 1 }}>
              Apple Dashboard Prototype
            </Typography>
          </Toolbar>
        </AppBar>
        <Container maxWidth="xl" sx={{ py: 4, height: '100vh' }}>
          <PanelGroup direction="horizontal" style={{ height: '60vh' }}>
            <Panel minSize={20} defaultSize={50} style={{ height: '100%' }}>
              <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, height: '100%' }}>
                <MainInfo />
                <FinancialStatements />
              </Box>
            </Panel>
            <PanelResizeHandle style={{ width: 6, background: '#e0e0e0', cursor: 'col-resize' }} />
            <Panel minSize={20} defaultSize={50} style={{ height: '100%' }}>
              <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, height: '100%' }}>
                <BullBearDebate />
              </Box>
            </Panel>
          </PanelGroup>
          <Box sx={{ mt: 3 }}>
            <ChartSection />
            <PeerComparison />
          </Box>
        </Container>
      </Box>
    </ThemeProvider>
  );
}

export default App;
