import React, { useState } from 'react';
import { 
  Card, 
  CardContent, 
  Typography, 
  Table, 
  TableBody, 
  TableCell, 
  TableContainer, 
  TableHead, 
  TableRow, 
  Paper,
  Button,
  Box,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  IconButton,
  Chip
} from '@mui/material';
import { Add, Delete, Edit } from '@mui/icons-material';

interface PeerData {
  name: string;
  marketCap: string;
  adv: string;
  epsFY1: string;
  epsFY2: string;
  peFY1: string;
  peFY2: string;
  [key: string]: string; // For dynamic columns
}

interface Column {
  id: string;
  label: string;
  type: 'text' | 'number' | 'percentage';
}

const PeerComparison: React.FC = () => {
  const [peers, setPeers] = useState<PeerData[]>([
    {
      name: "TechCorp Inc.",
      marketCap: "$45.2B",
      adv: "$125M",
      epsFY1: "6.20",
      epsFY2: "7.35",
      peFY1: "25.3",
      peFY2: "21.3"
    },
    {
      name: "Software Solutions Ltd.",
      marketCap: "$32.8B",
      adv: "$89M",
      epsFY1: "4.85",
      epsFY2: "5.70",
      peFY1: "28.1",
      peFY2: "23.9"
    },
    {
      name: "Digital Systems Corp.",
      marketCap: "$28.5B",
      adv: "$67M",
      epsFY1: "3.95",
      epsFY2: "4.65",
      peFY1: "31.2",
      peFY2: "26.5"
    },
    {
      name: "CloudTech Industries",
      marketCap: "$38.9B",
      adv: "$112M",
      epsFY1: "5.45",
      epsFY2: "6.40",
      peFY1: "26.8",
      peFY2: "22.8"
    }
  ]);

  const [columns, setColumns] = useState<Column[]>([
    { id: 'name', label: 'Name', type: 'text' },
    { id: 'marketCap', label: 'Market Cap', type: 'text' },
    { id: 'adv', label: 'ADV', type: 'text' },
    { id: 'epsFY1', label: 'EPS FY+1 y/y', type: 'percentage' },
    { id: 'epsFY2', label: 'EPS FY+2 y/y', type: 'percentage' },
    { id: 'peFY1', label: 'P/E FY+1', type: 'number' },
    { id: 'peFY2', label: 'P/E FY+2', type: 'number' }
  ]);

  const [openAddColumn, setOpenAddColumn] = useState(false);
  const [openAddPeer, setOpenAddPeer] = useState(false);
  const [newColumn, setNewColumn] = useState({ id: '', label: '', type: 'text' as const });
  const [newPeer, setNewPeer] = useState<PeerData>({
    name: '',
    marketCap: '',
    adv: '',
    epsFY1: '',
    epsFY2: '',
    peFY1: '',
    peFY2: ''
  });

  const handleAddColumn = () => {
    if (newColumn.id && newColumn.label) {
      setColumns([...columns, newColumn]);
      setPeers(peers.map(peer => ({ ...peer, [newColumn.id]: '' })));
      setNewColumn({ id: '', label: '', type: 'text' });
      setOpenAddColumn(false);
    }
  };

  const handleAddPeer = () => {
    if (newPeer.name) {
      const peerWithAllColumns = { ...newPeer };
      columns.forEach(col => {
        if (!peerWithAllColumns[col.id]) {
          peerWithAllColumns[col.id] = '';
        }
      });
      setPeers([...peers, peerWithAllColumns]);
      setNewPeer({
        name: '',
        marketCap: '',
        adv: '',
        epsFY1: '',
        epsFY2: '',
        peFY1: '',
        peFY2: ''
      });
      setOpenAddPeer(false);
    }
  };

  const handleDeletePeer = (index: number) => {
    setPeers(peers.filter((_, i) => i !== index));
  };

  const handleDeleteColumn = (columnId: string) => {
    if (columnId !== 'name') { // Prevent deleting the name column
      setColumns(columns.filter(col => col.id !== columnId));
      setPeers(peers.map(peer => {
        const { [columnId]: removed, ...rest } = peer;
        return rest;
      }));
    }
  };

  const formatValue = (value: string, type: string): string => {
    if (!value) return '-';
    
    switch (type) {
      case 'percentage':
        return `${value}%`;
      case 'number':
        return value;
      default:
        return value;
    }
  };

  return (
    <Card sx={{ mb: 3 }}>
      <CardContent>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
          <Typography variant="h5" component="h2">
            Peer Comparison
          </Typography>
          <Box sx={{ display: 'flex', gap: 1 }}>
            <Button
              variant="outlined"
              startIcon={<Add />}
              onClick={() => setOpenAddColumn(true)}
              size="small"
            >
              Add Column
            </Button>
            <Button
              variant="outlined"
              startIcon={<Add />}
              onClick={() => setOpenAddPeer(true)}
              size="small"
            >
              Add Peer
            </Button>
          </Box>
        </Box>

        <TableContainer component={Paper} sx={{ maxHeight: 500 }}>
          <Table stickyHeader size="small">
            <TableHead>
              <TableRow>
                {columns.map((column) => (
                  <TableCell 
                    key={column.id} 
                    align="center" 
                    sx={{ 
                      fontWeight: 'bold', 
                      minWidth: column.id === 'name' ? 150 : 120,
                      position: 'relative'
                    }}
                  >
                    <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                      {column.label}
                      {column.id !== 'name' && (
                        <IconButton
                          size="small"
                          onClick={() => handleDeleteColumn(column.id)}
                          sx={{ ml: 0.5, p: 0.5 }}
                        >
                          <Delete fontSize="small" />
                        </IconButton>
                      )}
                    </Box>
                  </TableCell>
                ))}
                <TableCell align="center" sx={{ minWidth: 80 }}>Actions</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {peers.map((peer, index) => (
                <TableRow key={index} hover>
                  {columns.map((column) => (
                    <TableCell key={column.id} align="center">
                      <Typography
                        sx={{
                          fontWeight: column.id === 'name' ? 'bold' : 'normal',
                          color: peer[column.id] ? 'inherit' : 'text.secondary'
                        }}
                      >
                        {formatValue(peer[column.id], column.type)}
                      </Typography>
                    </TableCell>
                  ))}
                  <TableCell align="center">
                    <IconButton
                      size="small"
                      onClick={() => handleDeletePeer(index)}
                      color="error"
                    >
                      <Delete fontSize="small" />
                    </IconButton>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>

        <Box sx={{ mt: 2, display: 'flex', gap: 1, flexWrap: 'wrap' }}>
          <Chip label="Dynamic Columns" color="primary" variant="outlined" />
          <Chip label="Add/Remove Peers" color="success" size="small" />
          <Chip label="Real-time Updates" color="info" size="small" />
        </Box>

        {/* Add Column Dialog */}
        <Dialog open={openAddColumn} onClose={() => setOpenAddColumn(false)} maxWidth="sm" fullWidth>
          <DialogTitle>Add New Column</DialogTitle>
          <DialogContent>
            <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mt: 1 }}>
              <TextField
                label="Column ID"
                value={newColumn.id}
                onChange={(e) => setNewColumn({ ...newColumn, id: e.target.value })}
                placeholder="e.g., revenue, margin"
              />
              <TextField
                label="Column Label"
                value={newColumn.label}
                onChange={(e) => setNewColumn({ ...newColumn, label: e.target.value })}
                placeholder="e.g., Revenue, Operating Margin"
              />
              <FormControl>
                <InputLabel>Data Type</InputLabel>
                <Select
                  value={newColumn.type}
                  label="Data Type"
                  onChange={(e) => setNewColumn({ ...newColumn, type: e.target.value as any })}
                >
                  <MenuItem value="text">Text</MenuItem>
                  <MenuItem value="number">Number</MenuItem>
                  <MenuItem value="percentage">Percentage</MenuItem>
                </Select>
              </FormControl>
            </Box>
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setOpenAddColumn(false)}>Cancel</Button>
            <Button onClick={handleAddColumn} variant="contained">Add Column</Button>
          </DialogActions>
        </Dialog>

        {/* Add Peer Dialog */}
        <Dialog open={openAddPeer} onClose={() => setOpenAddPeer(false)} maxWidth="md" fullWidth>
          <DialogTitle>Add New Peer</DialogTitle>
          <DialogContent>
            <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mt: 1 }}>
              {columns.map((column) => (
                <TextField
                  key={column.id}
                  label={column.label}
                  value={newPeer[column.id] || ''}
                  onChange={(e) => setNewPeer({ ...newPeer, [column.id]: e.target.value })}
                  required={column.id === 'name'}
                  placeholder={column.type === 'percentage' ? 'e.g., 15.5' : 'Enter value'}
                />
              ))}
            </Box>
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setOpenAddPeer(false)}>Cancel</Button>
            <Button onClick={handleAddPeer} variant="contained">Add Peer</Button>
          </DialogActions>
        </Dialog>
      </CardContent>
    </Card>
  );
};

export default PeerComparison;
